    &#125;

    //Make the prefix of the map
    for (int i = 1; i &lt;= bd; i++)
    &#123;
        for (int j = 1; j &lt;= bd; j++)
        &#123;
            map[i][j] += map[i-1][j] + map[i][j-1] - map[i-1][j-1];
        &#125;
    &#125;

