private void sink(int k)
&#123;
    while (2*k &lt;= N)
    &#123;
        int j = 2*k;
        if (j &lt; N &amp;&amp; less(j,j+1)) j++;
        if (!less(k,j)) break;
        exch(k,j);
        k = j;
    &#125;
