    public int[] searchRange(int[] nums, int target) &#123;
        int left = 0;
        int right = nums.length - 1;
        int mid;
        int l_bound;
        int r_bound;
        int[] result = new int[2];
        while (left &lt;= right) &#123;
            mid = left + (right - left) / 2;
            if (nums[mid] &lt; target) left = mid + 1;
            else if (nums[mid] > target) right = mid - 1;
            else if (nums[mid] == target) &#123;
                l_bound = mid;
                r_bound = mid;
                while (nums[l_bound] == target &amp;&amp; l_bound > left) &#123;
                    l_bound--;
                &#125;
                if (nums[l_bound] != target) l_bound++; //è¾¹ç•Œæ£€æŸ¥
                while (nums[r_bound] == target &amp;&amp; r_bound &lt; right) &#123;
                    r_bound++;
                &#125;
                if (nums[r_bound] != target) r_bound--; //è¾¹ç•Œæ£€æŸ¥
                result[0] = l_bound;
                result[1] = r_bound;
                return result;
            &#125;
        &#125;
        result[0] = -1;
        result[1] = -1;
        return result;
