    public int numJewelsInStones(String J, String S) &#123;
        if (J.length() == 0) return 0;
        if (S.length() == 0) return 0;
        int diamond_nums = 0;
        HashMap&lt;Character, Integer> diamond = new HashMap&lt;Character, Integer>();
        for (int i = 0; i &lt; J.length(); i++) &#123;
            diamond.put(J.charAt(i), 0);
        &#125;
        for (int i = 0; i &lt; S.length(); i++) &#123;
            if (diamond.containsKey(S.charAt(i))) &#123;
                diamond_nums++;
            &#125;
        &#125;
