class Solution &#123;
    public boolean containsDuplicate(int[] nums) &#123;
        int a = 0;
        int len = nums.length;
        int b = 1;

        if (len == 0 || len == 1) return false;

        Arrays.sort(nums);

        while (b &lt; len) &#123;
            if (nums[a] == nums[b]) &#123;
                return true;
            &#125; else &#123;
                a++;
                b++;
            &#125;
        &#125;
        if (b == len - 1) &#123;
            return false;
        &#125;
        return false;
