class Solution &#123;
    public int firstUniqChar(String s) &#123;
        HashMap&lt;Character,Integer> record = new HashMap&lt;Character,Integer>();
        int len = s.length();
        for (int i = 0; i &lt; len; i++) &#123;
            char tmp = s.charAt(i);
            record.put(tmp, record.getOrDefault(tmp,0)+1);
        &#125;
        for (int i = 0; i &lt; len; i++) &#123;
            if (record.get(s.charAt(i)) == 1) &#123;
