int pivotIndex(int* nums, int numsSize)&#123;
    int left_sum = 0;
    int right_sum = 0;
    int i = 0, l_piv = 0, r_piv = 0;
    int all_sum = 0;
    for (int k = 0; k &lt; numsSize; k++) &#123;
        all_sum += nums[k];
    &#125;
    for (i = 0; i &lt; numsSize; i++) &#123;
        for (l_piv = 0; l_piv &lt; i; l_piv++) &#123;
            left_sum += nums[l_piv];
        &#125;
        right_sum = all_sum - left_sum - nums[i];
        // for (r_piv = numsSize-1; r_piv > l_piv; r_piv--) &#123;
        //     right_sum += nums[r_piv];
        // &#125;
        if (left_sum == right_sum) &#123;
            return l_piv;
            break;
        &#125;
        left_sum = 0;
        right_sum = 0;
        //printf(\n);
    &#125;
    if (i == numsSize) &#123;
        return -1;
    &#125;
    return 0;
&#125;

